import React, { useState } from 'react';
import './SignUpForm.css';
import axios from 'axios';

const SignUpForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [internetPackage, setInternetPackage] = useState('basic');
  const [cablePackage, setCablePackage] = useState('cable');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/create-customer/', {
        name: name,
        email: email,
        phone_number: phoneNumber,
        address: address,
        internet_package: internetPackage,
        cable_package: cablePackage
      });
      console.log(response.data); // Assuming the response data is logged in the console
    } catch (error) {
      console.error('Error creating customer:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <br />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Phone Number"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
      />
      <br />
      <textarea
        placeholder="Address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
      />
      <br />
      <select value={internetPackage} onChange={(e) => setInternetPackage(e.target.value)}>
        <option value="basic">Basic</option>
        <option value="standard">Standard</option>
        <option value="premium">Premium</option>
        <option value="void">Void</option>
      </select>
      <br />
      <select value={cablePackage} onChange={(e) => setCablePackage(e.target.value)}>
        <option value="cable">Yes</option>
        <option value="no-cable">No</option>
      </select>
      <br />
      <button type="submit">Create Customer</button>
    </form>
  );
};

export default SignUpForm;
